void doEncoderA_M1(){    if (digitalRead(encoderPinA_M1) == HIGH)  {    if (digitalRead(encoderPinB_M1) == HIGH)      position_M1 = position_M1 + 1;    else      position_M1 = position_M1 - 1;     }    else   {    if (digitalRead(encoderPinB_M1) == LOW)      position_M1 = position_M1 + 1;    else      position_M1 = position_M1 - 1;       }}void doEncoderB_M1(){    if (digitalRead(encoderPinB_M1) == HIGH)  {    if (digitalRead(encoderPinA_M1) == LOW)      position_M1 = position_M1 + 1;    else      position_M1 = position_M1 - 1;       }    else   {    if (digitalRead(encoderPinA_M1) == HIGH)      position_M1 = position_M1 + 1;    else      position_M1 = position_M1 - 1;       }}void doEncoderA_M2(){    if (digitalRead(encoderPinA_M2) == HIGH)  {    if (digitalRead(encoderPinB_M2) == HIGH)      position_M2 = position_M2 + 1;    else      position_M2 = position_M2 - 1;     }    else   {    if (digitalRead(encoderPinB_M2) == LOW)      position_M2 = position_M2 + 1;    else      position_M2 = position_M2 - 1;       }}void doEncoderB_M2(){    if (digitalRead(encoderPinB_M2) == HIGH)  {    if (digitalRead(encoderPinA_M2) == LOW)      position_M2 = position_M2 + 1;    else      position_M2 = position_M2 - 1;       }    else   {    if (digitalRead(encoderPinA_M2) == HIGH)      position_M2 = position_M2 + 1;    else      position_M2 = position_M2 - 1;       }}